#include <iostream>
#include <vector>
using namespace std;

void myQSort(vector<int> &A,int left,int right)
{
    if(left>=right)
        return;
    int i=left;
    int j=right;
    int temp;
    while(i<j)
    {
        while(j>i&&A[j]<A[left])
            j--;
        while(i<j&&A[i]>=A[left])
            i++;
        temp=A[i];
        A[i]=A[j];
        A[j]=temp;
    }
    temp=A[i];
    A[i]=A[left];
    A[left]=temp;
    myQSort(A, left, i-1);
    myQSort(A, i+1, right);
}

int largestPerimeter(vector<int>& A) {
    int n=int(A.size());
    if(n<3)
        return 0;
    myQSort(A, 0, n-1);
    for(int i=0;i<n-2;i++)
    {
        if(A[i+1]+A[i+2]>A[i])
            return A[i]+A[i+1]+A[i+2];
    }
    return  0;
}

